import React, { ChangeEvent, KeyboardEvent, useRef, useState } from 'react'
import './style.css'
import InputBox from 'components/InputBox';
import { SignInRequestDto } from 'apis/request/auth';
import { signInRequest } from 'apis';
import { SignInResponseDto } from 'apis/response/auth';
import { ResponseDto } from 'apis/response';
import { useCookies } from 'react-cookie';
import { MAIN_PATH } from 'constant';
import { useNavigate } from 'react-router-dom';
import { Address, useDaumPostcodePopup } from 'react-daum-postcode';

//          component: ì¸ì¦ í™”ë©´ ì»´í¬ë„ŒíŠ¸          //
export default function Authentication() {

  //          state: í™”ë©´ ìƒíƒœ          //
  const [view, setView] = useState<'sign-in' | 'sign-up'>('sign-in')

  //          state: ì¿ í‚¤ ìƒíƒœ          //
  const [ cookies, setCookie] = useCookies();

  //           function: ë„¤ë¹„ê²Œì´íŠ¸ í•¨ìˆ˜          //
  const navigator = useNavigate();

  //          component: sign in card ì»´í¬ë„ŒíŠ¸          //
  const SignInCard = () => {

    //          state: ì´ë©”ì¼ ìš”ì†Œ ì°¸ì¡° ìƒíƒœ          //
    const emailRef = useRef<HTMLInputElement | null>(null);
    //          state: íŒ¨ìŠ¤ì›Œë“œ ìš”ì†Œ ì°¸ì¡° ìƒíƒœ          //
    const passwordRef = useRef<HTMLInputElement | null>(null);
    //          state: ì´ë©”ì¼ ìƒíƒœ          //
    const [email, setEmail] = useState<string>('');
    //          state: íŒ¨ìŠ¤ì›Œë“œ ìƒíƒœ          //
    const [password, setPassword] = useState<string>('');
    //          state: íŒ¨ìŠ¤ì›Œë“œ íƒ€ì… ìƒíƒœ          //
    const [passwordType, setPasswordType] = useState<'text' | 'password'>('password');
    //          state: íŒ¨ìŠ¤ì›Œë“œ ë²„íŠ¼ ì•„ì´ì½˜ ìƒíƒœ          //
    const [passwordButtonIcon, setPasswordButtonIcon] = useState<'eye-light-off-icon' | 'eye-light-on-icon'>('eye-light-off-icon');
    //          state: ì—ëŸ¬ ìƒíƒœ          //
    const [error, setError] = useState<boolean>(false);
    

    //          function: sign in response ì²˜ë¦¬ í•¨ìˆ˜           //
    const signInResponse = (responseBody: SignInResponseDto | ResponseDto | null) => {
      if (!responseBody) {
        alert('ë„¤íŠ¸ì›Œí¬ ì´ìƒì…ë‹ˆë‹¤.');
        return;
      }
      const { code } = responseBody;
      if (code === 'DBE') alert('ë°ì´í„°ë² ì´ìŠ¤ ì˜¤ë¥˜ì…ë‹ˆë‹¤.');
      if (code === 'SF' || code === 'VF') setError(true);
      if (code !== 'SU') return;

      const { token, expirationTime } = responseBody as SignInResponseDto;
      const now = new Date().getTime();
      const expires = new Date(now + expirationTime * 1000);

      setCookie('accessToken', token, { expires, path: MAIN_PATH() });
      navigator(MAIN_PATH());

    }

    //          event handler: ì´ë©”ì¼ ë³€ê²½ ì´ë²¤íŠ¸ ì²˜ë¦¬ í•¨ìˆ˜          //
    const onEmailChangeHandler = (event: ChangeEvent<HTMLInputElement>) => {
      setError(false);
      const { value } = event.target;
      setEmail(value);
    }
    //          event handler: íŒ¨ìŠ¤ì›Œë“œ ë³€ê²½ ì´ë²¤íŠ¸ ì²˜ë¦¬ í•¨ìˆ˜          //
    const onPasswordChangeHandler = (event: ChangeEvent<HTMLInputElement>) => {
      setError(false);
      const { value } = event.target;
      setPassword(value);
    }
    //          event handler: ë¡œê·¸ì¸ ë²„íŠ¼ í´ë¦­ ì´ë²¤íŠ¸ ì²˜ë¦¬ í•¨ìˆ˜          //
    const onSignInButtonClickHandler = () => {
        const requestBody: SignInRequestDto = { email, password };
        signInRequest(requestBody).then(signInResponse);
    }
    //          event handler: íšŒì›ê°€ì… ë§í¬ í´ë¦­ ì´ë²¤íŠ¸ ì²˜ë¦¬ í•¨ìˆ˜          //
    const onSignUpLinkClickHandler = () => {
      setView('sign-up');
    }
    //          event handler: íŒ¨ìŠ¤ì›Œë“œ ë²„íŠ¼ í´ë¦­ ì´ë²¤íŠ¸ ì²˜ë¦¬ í•¨ìˆ˜          //
    const onPasswordButtonClickHandler = () => {
      if(passwordType === 'text'){
        setPasswordType('password');
        setPasswordButtonIcon('eye-light-off-icon');
      }
      else{
        setPasswordType('text');
        setPasswordButtonIcon('eye-light-on-icon');
      }
    }
    //          event handler: ì´ë©”ì¼ ì¸í’‹ í‚¤ ë‹¤ìš´ ì´ë²¤íŠ¸ ì²˜ë¦¬ í•¨ìˆ˜          //
    const onEmailKeyDownHandler = (event: KeyboardEvent<HTMLInputElement>) => {
      if (event.key !== 'Enter') return;
      if (!passwordRef.current) return;
      passwordRef.current.focus();
    }
    //          event handler: íŒ¨ìŠ¤ì›Œë“œ ì¸í’‹ í‚¤ ë‹¤ìš´ ì´ë²¤íŠ¸ ì²˜ë¦¬ í•¨ìˆ˜          //
    const onPasswordKeyDownHandler = (event: KeyboardEvent<HTMLInputElement>) => {
      if (event.key !== 'Enter') return;
      onSignInButtonClickHandler();
    }

    //          render: sign in card ì»´í¬ë„ŒíŠ¸ ë Œë”ë§          //  
    return (
      <div className='auth-card'>
        <div className='auth-card-box'>
          <div className='auth-card-top'>
            <div className='auth-card-title-box'>
              <div className='auth-card-title'>{'ë¡œê·¸ì¸'}</div>
            </div>
            <InputBox ref={emailRef} label='ì´ë©”ì¼ ì£¼ì†Œ' type='text' placeholder='ì´ë©”ì¼ ì£¼ì†Œë¥¼ ì…ë ¥í•´ì£¼ì„¸ìš”.' error={error} value={email} onChange={onEmailChangeHandler} onKeyDown={onEmailKeyDownHandler} />
            <InputBox ref={passwordRef} label='ë¹„ë°€ë²ˆí˜¸' type={passwordType} placeholder='ë¹„ë°€ë²ˆí˜¸ë¥¼ ì…ë ¥í•´ì£¼ì„¸ìš”.' error={error} value={password} onChange={onPasswordChangeHandler} icon={passwordButtonIcon} onButtonClick={onPasswordButtonClickHandler} onKeyDown={onPasswordKeyDownHandler} />
          </div>
          <div className='auth-card-bottom'>
            {error && 
            <div className='auth-sign-in-error-box'>
              <div className='auth-sign-in-error-message'>
                {'ì´ë©”ì¼ ì£¼ì†Œ ë˜ëŠ” ë¹„ë°€ë²ˆí˜¸ë¥¼ ì˜ëª» ì…ë ¥í–ˆìŠµë‹ˆë‹¤.\nì…ë ¥í•˜ì‹  ë‚´ìš©ì„ ë‹¤ì‹œ í™•ì¸í•´ì£¼ì„¸ìš”.'}
              </div>  
            </div>  
            }
            <div className='black-large-full-button' onClick={onSignInButtonClickHandler}>{'ë¡œê·¸ì¸'}</div>
            <div className='auth-description-box'>
              <div className='auth-description'>{'ì‹ ê·œ ì‚¬ìš©ì ì´ì‹ ê°€ìš”? '}
                <span className='auth-description-link' onClick={onSignUpLinkClickHandler}>{'íšŒì›ê°€ì…'}</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  //          component: sign up card ì»´í¬ë„ŒíŠ¸          //
  const SignUpCard = () => {

    //          state: ì´ë©”ì¼ ìš”ì†Œ ì°¸ì¡° ìƒíƒœ          //
    const emailRef = useRef<HTMLInputElement | null>(null);
    //          state: íŒ¨ìŠ¤ì›Œë“œ ìš”ì†Œ ì°¸ì¡° ìƒíƒœ          //
    const passwordRef = useRef<HTMLInputElement | null>(null);
    //          state: íŒ¨ìŠ¤ì›Œë“œ í™•ì¸ ìš”ì†Œ ì°¸ì¡° ìƒíƒœ          //
    const passwordCheckRef = useRef<HTMLInputElement | null>(null);
    //          state: ë‹‰ë„¤ì„ ìš”ì†Œ ì°¸ì¡° ìƒíƒœ          //
    const nicknameRef = useRef<HTMLInputElement | null>(null);
    //          state: ì „í™”ë²ˆí˜¸ ìš”ì†Œ ì°¸ì¡° ìƒíƒœ          //
    const telNumberRef = useRef<HTMLInputElement | null>(null);
    //          state: ì£¼ì†Œ ìš”ì†Œ ì°¸ì¡° ìƒíƒœ          //
    const addressRef = useRef<HTMLInputElement | null>(null);
    //          state: ìƒì„¸ ì£¼ì†Œ ìš”ì†Œ ì°¸ì¡° ìƒíƒœ          //
    const addressDetailRef = useRef<HTMLInputElement | null>(null);

    //          state: í˜ì´ì§€ ë²ˆí˜¸ ìƒíƒœ           //
    const [page, setPage] = useState<1 | 2>(2);

    //          state: ì´ë©”ì¼ ìƒíƒœ           //
    const [email, setEmail] = useState<string>('');
    //          state: íŒ¨ìŠ¤ì›Œë“œ ìƒíƒœ           //
    const [password, setPassword] = useState<string>('');
    //          state: íŒ¨ìŠ¤ì›Œë“œ í™•ì¸ ìƒíƒœ           //
    const [passwordCheck, setPasswordCheck] = useState<string>('');
    //          state: ë‹‰ë„¤ì„ ìƒíƒœ           //
    const [nickname, setNickname] = useState<string>('');
    //          state: íœ´ëŒ€ì „í™”ë²ˆí˜¸ ìƒíƒœ           //
    const [telNumber, setTelNumber] = useState<string>('');
    //          state: ì£¼ì†Œ ìƒíƒœ           //
    const [address, setAddress] = useState<string>('');
    //          state: ìƒì„¸ ì£¼ì†Œ ìƒíƒœ           //
    const [addressDetail, setAddressDetail] = useState<string>('');
    //          state: ê°œì¸ ì •ë³´ ë™ì˜ ìƒíƒœ           //
    const [agreedPersonal, setAgreedPersonal] = useState<boolean>(false);

    //          state: íŒ¨ìŠ¤ì›Œë“œ íƒ€ì… ìƒíƒœ           //
    const [passwordType, setPasswordType] = useState<'text' | 'password'>('password');
    //          state: íŒ¨ìŠ¤ì›Œë“œ í™•ì¸ íƒ€ì… ìƒíƒœ           //
    const [passwordCheckType, setPasswordCheckType] = useState<'text' | 'password'>('password');

