import React, { useEffect, useRef, useState } from 'react'
import './style.css'
import { User } from 'types/interface';
import defaultProfileImage from 'assets/image/default-profile-image.png'
import { useParams } from 'react-router-dom';

//          component: 유저 화면 컴포넌트          //
export default function UserPage() {

  //          state: 마이페이지 여부 상태          //
  const { userEmail } = useParams();

  //          component: 유저 화면 상단 컴포넌트          //
  const UserTop = () => {

    //          state: 이미지 파일 인풋 참조 상태         //
    const imageInputRef = useRef<HTMLInputElement | null>(null);
    //          state: 유저페이지 여부 상태          //
    const [isMyPage, setMyPage] = useState<boolean>(true);
    //          state: 유저 정보 상태          //
    const [user, setUser] = useState<User | null>(null);

    //          effect: user email path variable 변경 시 실행할 함수          //
    useEffect(() => {

      if (!userEmail) return;
      setUser({email: 'wjdwns0531@naver.com', nickname: '상추새벽', profileImage: 'https://i.ibb.co/gLnpbfF6/Kakao-Talk-20250107-210432147.jpg'})

    }, [userEmail]);

    //          render: 유저 화면 상단 컴포넌트 렌더링          //
    if (!user) return(<></>);
    return (
      <div id='user-top-wrapper'>
        <div className='user-top-container'>
          {isMyPage ? 
          <div className='user-top-my-profile-image-box'>
            {user.profileImage !== null ? 
            <div className='user-top-profile-image' style={{ backgroundImage: `(${user.profileImage})` }}></div> : 
            <div className='user-top-my-profile-image-nothing' style={{ backgroundImage: `(${defaultProfileImage})` }}></div>
            }
            <input ref={imageInputRef} type='file' accept='image/*' style={{display: 'none'}} /> 
          </div> :
          <div className='user-top-profile-image-box' style={{ backgroundImage: `(${user.profileImage})` }}></div>
          }
          <div className='user-top-info-box'>
            <div className='user-top-info-nickname-box'>
              <div className='user-top-info-nickname'>{user.nickname}</div>
              {isMyPage &&
              <div className='icon-button'>
                <div className='icon edit-icon'></div>
              </div>
              }
            </div>
            <div className='user-top-info-email'>{user.email}</div>
          </div>
        </div>
      </div>
    )

  }

  //          component: 유저 화면 하단 컴포넌트          //
  const UserBottom = () => {

    //          render: 유저 화면 하단 컴포넌트 렌더링          //  
    return (
      <div>유저 화면 하단</div>
    )

  }
  //          render: 유저 화면 컴포넌트 렌더링          //  
  return (

    <>
      <UserTop />
      <UserBottom />
    </>
    
  )
}
