import { ChangeEvent, useEffect, useRef, useState } from 'react'
import './style.css'
import useBoardStore from 'stores/board.store';


//          component: 게시물 작성 화면 컴포넌트          //
export default function BoardWrite() {

  //          state: 본문 영역 요소 참조 상태           //
  const titleRef = useRef<HTMLTextAreaElement | null>(null);
  //          state: 본문 영역 요소 참조 상태           //
  const contentRef = useRef<HTMLTextAreaElement | null>(null);
  //          state: 이미지 입력 요소 참조 상태           //
  const imageInputRef = useRef<HTMLInputElement | null>(null);

  //          state: 게시물 상태          //
  const { title, setTitle } = useBoardStore(); 
  const { content, setContent } = useBoardStore();
  const { boardImageFileList, setBoardImageFileList } = useBoardStore();
  const { resetBoard } = useBoardStore();

  //          state: 게시물 이미지 미리보기 URL 상태          //
  const [imageUrls, setImageUrls] = useState<string[]>([]);

  //          event handler: 제목 변경 이벤트 처리         //
  const onTitleChangeHandler = (event:ChangeEvent<HTMLTextAreaElement>) => {
    const { value } = event.target;
    setTitle(value);

    if (!contentRef.current) return;
    contentRef.current.style.height = 'auto';
    contentRef.current.style.height = `${contentRef.current.scrollHeight}px`
  }
  //          event handler: 내용 변경 이벤트 처리         //
  const onContentChangeHandler = (event:ChangeEvent<HTMLTextAreaElement>) => {
    const { value } = event.target;
    setContent(value);

    if (!contentRef.current) return;
    contentRef.current.style.height = 'auto';
    contentRef.current.style.height = `${contentRef.current.scrollHeight}px`
  }

  //          effect: 마운트 시 실행할 함수          //
  useEffect(() => {
    resetBoard();
  }, []);

  //          render: 게시물 작성 화면 컴포넌트 렌더링          //  
  return (
    <div id='board-write-wrapper'>
      <div className='board-write-container'>
        <div className='board-write-box'>
          <div className='board-write-title-box'>
            <textarea ref={titleRef} className='board-write-title-textarea' rows={1} placeholder='제목을 작성해주세요.' value={title} onChange={onTitleChangeHandler}/>
          </div>
          <div className='divider'></div>
          <div className='board-write-content-box'>
            <textarea ref={contentRef} className='board-write-content-textarea' placeholder='본문을 작성해주세요.' value={content} onChange={onContentChangeHandler}/>
            <div className='icon-button'>
              <div className='icon image-box-light-icon'></div>
            </div>
            <input ref={imageInputRef} type='file' accept='image/*' style={{ display: 'none' }} />
          </div>
          <div className='board-write-images-box'>
            <div className='board-write-image-box'>
              <img className='board-write-image' src='https://i.ytimg.com/vi/BU1LaDOXGA0/sddefault.jpg'/>
              <div className='icon-button image-close'>
                <div className='icon close-icon'></div>
              </div>
            </div>
            <div className='board-write-image-box'>
              <img className='board-write-image' src='https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT5nQCTLUUwgY-muPO75qGz7L4hKyc5cHs8-w&s'/>
              <div className='icon-button image-close'>
                <div className='icon close-icon'></div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
