import Top3Item from 'components/Top3Item'
import './style.css'
import { useEffect, useState } from 'react'
import { BoardListItem } from 'types/interface'
import { top3BoardListMock } from 'mocks'
import BoardItem from 'components/BoardItem'

//          component: 메인 화면 컴포넌트          //
export default function Main() {

  //          component: 메인 화면 상단 컴포넌트          //
  const MainTop = () => {

  //          state: 주간 top3 게시물 리스트 상태          //
  const [top3BoardList, setTop3BoardList] = useState<BoardListItem[]>([]);

  //          effect: 첫 마운트 시 실행될 함수          //
  useEffect(() => {
    setTop3BoardList(top3BoardListMock);
  }, [])

  //          render: 메인 화면 상단 컴포넌트 렌더링          //  
  return (
    <div id='main-top-wrapper'>
      <div className='main-top-container'>
        <div className='main-top-title'>{`Jun's board에서\n다양한 이야기를 나눠보세요.`}</div>
        <div className='main-top-contents-box'>
          <div className='main-top-contents-title'>{`주간 TOP 3 게시물`}</div>
          <div className='main-top-contents'>
            {top3BoardList.map(top3ListItem => <Top3Item top3ListItem={top3ListItem} />)}
          </div>
        </div>
      </div>
    </div>
  )

  }

  //          component: 메인 화면 하단 컴포넌트          //
  const MainBottom = () => {
  
  //          state: 최근 게시물 리스트 상태          //
  const [currentBoardList, setCurrentBoardList] = useState<BoardListItem[]>([]);

  //          effect: 첫 마운트 시 실행될 함수          //
  useEffect(() => {
    setTop3BoardList(top3BoardListMock);
  }, [])

  //          render: 메인 화면 하단 컴포넌트 렌더링          //  
  return (
    <div id='main-bottom-wrapper'>
      <div className='main-bottom-container'>
        <div className='main-bottom-title'>{`최근 게시물`}</div>
        <div className='main-bottom-contents-box'>
          <div className='main-bottom-current-board-box'>
            <BoardItem boardListItem={boardListItem}/>
          </div>
          <div className='main-bottom-popular-word-box'>
            <div className='main-bottom-popular-word-card'>
              <div className='main-bottom-popular-word-card-box'>
                <div className='main-bottom-popular-word-card-title'>{`인기 검색어`}</div>
                <div className='main-bottom-popular-word-card-contents'>
                  <div className='word-badge'></div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className='main-bottom-pagination-box'></div>
      </div>
    </div>
  )

  }

  //          render: 메인 화면 컴포넌트 렌더링          //  
  return (
    <>
      <MainTop />
      <MainBottom />
    </>
  )
}
