import { useState } from 'react'
import './style.css'
import { useNavigate } from 'react-router-dom'
import { MAIN_PATH } from 'constant';

//          component: 헤더 레이아웃          //
export default function Header() {

  //          function: 네비게이트 함수          //
  const navigate = useNavigate();

  //          event handler: 로고 클릭 이벤트 처리 함수          //
  const onLogoClickHandler = () => {
    navigate(MAIN_PATH());
  }

  //          component: 검색 버튼 컴포넌트         //
  const SearchButton = () => {

    //          state: 검색 버튼 상태          //
    const [status, setStatus] = useState<boolean>(false);

    if (!status)
    //          render: 검색 버튼 컴포넌트 렌더링 (클릭 Flase 상태)          //
    return <div className='icon-button'><div className='iconsearch-light-icon'></div></div>;
    //          render: 검색 버튼 컴포넌트 렌더링 (클릭 True 상태)          //
    return (
      <div className='header-search-input-box'>
        <input className='header-search-input'/>
        <div className='icon-button'>
          <div className='iconsearch-light-icon'></div>
        </div>;
      </div>
    );
  }

  //          render: 헤더 레이아웃 렌더링          //
  return (
    <div id='header'>
      <div className='header-container'> 
        <div className='header-left-box' onClick={onLogoClickHandler}>
          <div className='icon-box'>
            <div className='icon logo-dark-icon'> </div>
          </div>
          <div className='header-logo'>{'Jun\'s Board'}</div>
        </div>
        <div className='header-right-box'></div>
      </div>
    </div>
  )
}
