import { useParams } from 'react-router-dom'
import './style.css'
import { useEffect, useState } from 'react';
import { BoardListItem } from 'types/interface';
import { latestBoardListMock } from 'mocks';
import BoardItem from 'components/BoardItem';

//          component: 검색 화면 컴포넌트          //
export default function Search() {

  //          function: d          //

  //          state: searchWord path variable 상태          //
  const { searchWord } = useParams();
  //          state: 검색 게시물 개수 상태          //
  const [count, setCount] = useState<number>(2);
  //          state: 검색 게시물 리스트 상태          //
  const [searchBoardList, setSearchBoardList] = useState<BoardListItem[]>([]);
  //          state: 연관 검색어 리스트 상태          //
  const [relationList, setRelationList] = useState<string[]>([]);

  //          event handler: 인기 검색어 클릭 이벤트 처리          //
    const onPopularWordClickHandler = (word: string) => {
      navigate(SEARCH_PATH(word));
    }

  //          effect: 첫 마운트 시 실행될 함수          //
  useEffect(() => {
    setSearchBoardList(latestBoardListMock);
  }, []);

  //          render: 검색 화면 컴포넌트 렌더링          //
  if (!searchWord) return (<></>);
  return (
    <div id='search=wrapper'>
      <div className='search-container'>
        <div className='search-title-box'>
          <div className='search-title'><span className='emphasis'>{searchWord}</span>{`에 대한 검색 결과입니다.`}</div>
          <div className='search-count'>{count}</div>
        </div>
        <div className='search-contents-box'>
          {count === 0 ? 
          <div className='search-contents-nothing'>{`검색 결과가 없습니다.`}</div> :
          <div className='search-contents'>{searchBoardList.map(boardListItem => <BoardItem boardListItem={boardListItem} />)}</div>
          }
          <div className='search-relation-box'>
            <div className='search-relation-card'>
              <div className='search-relation-card-container'>
                <div className='search-relation-card-title'></div>
                {relationList.length === 0 ?
                <div className='search-relation-card-contents-nothing'></div> : 
                <div className='serach-relation-card-contents'>{relationList.map(word => <div className='word-badge' onClick={() => onPopularWordClickHandler(word)}>{word}</div>)}</div>
                }
              </div>
            </div>
          </div>
        </div>
        <div className='search-pagination-box'></div>
      </div>
    </div>
  )
}
